import React from 'react';
import Image from './Image';

class Pkmninfos extends React.Component {
  render() {
    const { name, type, image , averageWeight:value, averageWeight:measurementUnit } = this.props
    return (
      <div>
        <p>Name: {name} </p>
        <p>Type: {type} </p>
        <p>Weight: {value} {measurementUnit}</p>
        <Image source={image} alternativeText="User avatar" />
      </div>
    );
  }
}

export default Pkmninfos;
